module.exports = async (d) => {
  const code = d.command.code;

  const inside = d.unpack();
  const err = d.inside(inside);

  if (err) return d.error(err);

  if (d.client.variables[inside.inside] === undefined)
    return d.error(
      `:x: Invalid variable '${inside.inside}' in \`$resetGlobalUserVar${inside}\``
    );

  const all = await d.client.db.all({
    filter: (x) =>
      x.startsWith(`${inside.inside}_`) &&
      x.split("_").length < 3 &&
      !d.client.guilds.cache.has(x.split("_")[1]),
  });

  await Promise.all(all.map((x) => d.client.db.delete("main", x.key)));

  return {
    code: code.replaceLast(`$resetGlobalUserVar${inside}`, ""),
  };
};
